mapper.select:
select ddp.dealer_id as dealerId , ddp.product_id as producId , ddp.product_name as productName , ddp.price as price , ddp.season_id as seasonId , ev.event_date as eventDate , ev.event_aboutdate as eventAboutDate , ddpc.category_id as categoryId , cat.category_name as categoryName , cat.category_name_reading as categoryNameReading from wfs.dealers_detail_products ddp left join wfs.dealers_detail_products_categories ddpc on ddp.dealer_id=ddpc.dealer_id and ddp.product_id=ddpc.product_id inner join wfs.categories cat on ddpc.category_id=cat.category_id inner join wfs.event_dates ev on ddp.season_id=ev.event_date_id
ddp.product_name like #{productName}
and ddp.price > #{priceFrom}
and ddp.price < #{priceTo}
and ddp.season_id = #{seasonId}
and ddp.category_id = #{categoryId}
